              key={plan.id} 
              plan={plan} 
              onViewDetails={() => setSelectedPlanId(plan.id)}
            />
          ))}
        </div>
      ) : (
        <Card className="p-12">
          <EmptyState
            title="Hen√ºz plan yok"
            description="ƒ∞lk diyet planƒ±nƒ±zƒ± olu≈üturarak ba≈ülayƒ±n"
            action={
              <Button>
                <Plus className="w-4 h-4 mr-2" />
                Yeni Plan Olu≈ütur
              </Button>
            }
          />
        </Card>
      )}

      {/* Plan Preview */}
      {selectedPlan && (
        <Card className="p-6">
          <PlanPreview 
            plan={selectedPlan} 
            onClose={() => setSelectedPlanId(null)} 
          />
        </Card>
      )}
    </div>
  );
}

