import { useState } from "react";
import { ChevronLeft, ChevronRight } from "lucide-react";
import { Button } from "@/components/ui/button";
import { Card } from "@/components/ui/card";
import { useAppointments } from "@/lib/hooks/useAppointments";
import { format, startOfWeek, addDays, addWeeks, subWeeks, startOfMonth, endOfMonth, eachDayOfInterval, addMonths, subMonths, isSameMonth } from "date-fns";
import { tr } from "date-fns/locale";

export default function AppointmentCalendar() {
  const [currentDate, setCurrentDate] = useState(new Date());
  const [view, setView] = useState<"week" | "month">("week");

  const weekStart = startOfWeek(currentDate, { weekStartsOn: 1 });
  const weekDays = Array.from({ length: 7 }, (_, i) => addDays(weekStart, i));

  const monthStart = startOfMonth(currentDate);
  const monthEnd = endOfMonth(currentDate);
  const monthDays = eachDayOfInterval({ start: monthStart, end: monthEnd });

  // Calculate the full calendar grid for month view (6 weeks)
  const calendarStart = startOfWeek(monthStart, { weekStartsOn: 1 });
  const calendarEnd = addDays(calendarStart, 41); // 6 weeks = 42 days
  const calendarDays = eachDayOfInterval({ start: calendarStart, end: calendarEnd });

  const { data: appointments, isLoading } = useAppointments({
    from: view === "week" ? format(weekStart, "yyyy-MM-dd") : format(calendarStart, "yyyy-MM-dd"),
    to: view === "week" ? format(addDays(weekStart, 6), "yyyy-MM-dd") : format(calendarEnd, "yyyy-MM-dd"),
  });

  const timeSlots = Array.from({ length: 10 }, (_, i) => 
    `${String(9 + i).padStart(2, "0")}:00`
  );

  const navigate = (direction: "prev" | "next") => {
    setCurrentDate(prev => {
      if (view === "week") {
        return direction === "next" ? addWeeks(prev, 1) : subWeeks(prev, 1);
      } else {
        return direction === "next" ? addMonths(prev, 1) : subMonths(prev, 1);
      }
    });
  };

  const goToToday = () => {
    setCurrentDate(new Date());
  };

  if (isLoading) {
    return <div>Y√ºkleniyor...</div>;
  }

  return (
    <div className="space-y-6">
      {/* Calendar Controls */}
      <div className="flex items-center justify-between">
        <div className="flex items-center gap-4">
          <h4 className="text-xl font-semibold">
            {format(currentDate, "MMMM yyyy", { locale: tr })}
          </h4>
          <div className="flex gap-2">
            <Button 
              variant="outline" 
              size="icon"
              onClick={() => navigate("prev")}
              data-testid={`button-prev-${view}`}
            >
              <ChevronLeft className="w-4 h-4" />
            </Button>
            <Button 
              variant="outline" 
              size="icon"
              onClick={() => navigate("next")}
              data-testid={`button-next-${view}`}
            >
              <ChevronRight className="w-4 h-4" />
            </Button>
          </div>
        </div>
        <div className="flex items-center gap-3">
          <div className="flex rounded-md border border-border">
            <Button
              variant={view === "week" ? "default" : "ghost"}
              size="sm"
              onClick={() => setView("week")}
              className="rounded-r-none"
              data-testid="button-week-view"
            >
              Haftalƒ±k
            </Button>
            <Button
              variant={view === "month" ? "default" : "ghost"}
              size="sm"
              onClick={() => setView("month")}
              className="rounded-l-none"
              data-testid="button-month-view"
            >
              Aylƒ±k
            </Button>
          </div>
          <Button variant="outline" onClick={goToToday} data-testid="button-today">
            Bug√ºn
          </Button>
        </div>
      </div>

      {/* Weekly Calendar View */}
      {view === "week" && (
        <Card className="overflow-hidden">
          {/* Calendar Header */}
          <div className="grid grid-cols-8 border-b border-border">
            <div className="p-4 border-r border-border"></div>
            {weekDays.map((day, index) => {
              const isToday = format(day, "yyyy-MM-dd") === format(new Date(), "yyyy-MM-dd");
              return (
                <div
                  key={index}
                  className={`p-4 border-r border-border text-center last:border-r-0 ${
                    isToday ? "bg-primary/5" : ""
                  }`}
                >
                  <div className={`text-sm ${isToday ? "text-primary" : "text-muted-foreground"}`}>
                    {format(day, "E", { locale: tr })}
                  </div>
                  <div className={`text-lg font-semibold ${isToday ? "text-primary" : ""}`}>
                    {format(day, "d")}
                  </div>
                </div>
              );
            })}
          </div>

          {/* Time Slots */}
          <div className="grid grid-cols-8">
            {timeSlots.map((time, timeIndex) => (
              <div key={timeIndex} className="contents">
                <div className="p-3 border-r border-b border-border text-sm text-muted-foreground text-right">
                  {time}
                </div>
                {weekDays.map((day, dayIndex) => {
                  const isToday = format(day, "yyyy-MM-dd") === format(new Date(), "yyyy-MM-dd");
                  const dayAppointments = appointments?.filter((apt: any) => 
                    format(new Date(apt.startsAt), "yyyy-MM-dd") === format(day, "yyyy-MM-dd") &&
                    format(new Date(apt.startsAt), "HH") === time.split(":")[0]
                  ) || [];

                  return (
                    <div
                      key={`${timeIndex}-${dayIndex}`}
                      className={`p-2 border-r border-b border-border min-h-[80px] last:border-r-0 ${
                        isToday ? "bg-primary/5" : ""
                      }`}
                    >
                      {dayAppointments.map((appointment: any) => (
                        <div
                          key={appointment.id}
                          className="bg-chart-2/10 border border-chart-2 rounded p-2 text-xs mb-2"
                          data-testid={`appointment-${appointment.id}`}
                        >
                          <div className="font-medium text-chart-2">
                            {appointment.title}
                          </div>
                          <div className="text-muted-foreground">
                            {format(new Date(appointment.startsAt), "HH:mm")} - {format(new Date(appointment.endsAt), "HH:mm")}
                          </div>
                        </div>
                      ))}
                    </div>
                  );
                })}
              </div>
            ))}
          </div>
        </Card>
      )}

      {/* Monthly Calendar View */}
      {view === "month" && (
        <Card className="overflow-hidden">
          {/* Month Header - Days of Week */}
          <div className="grid grid-cols-7 border-b border-border">
            {["Pzt", "Sal", "√áar", "Per", "Cum", "Cmt", "Paz"].map((dayName, index) => (
              <div
                key={index}
                className="p-4 text-center text-sm font-medium text-muted-foreground border-r border-border last:border-r-0"
              >
                {dayName}
              </div>
            ))}
          </div>

          {/* Month Grid */}
          <div className="grid grid-cols-7">
            {calendarDays.map((day, index) => {
              const isToday = format(day, "yyyy-MM-dd") === format(new Date(), "yyyy-MM-dd");
              const isCurrentMonth = isSameMonth(day, currentDate);
              const dayAppointments = appointments?.filter((apt: any) => 
                format(new Date(apt.startsAt), "yyyy-MM-dd") === format(day, "yyyy-MM-dd")
              ) || [];

              return (
                <div
                  key={index}
                  className={`min-h-[120px] p-2 border-r border-b border-border last:border-r-0 ${
                    isToday ? "bg-primary/5" : ""
                  } ${!isCurrentMonth ? "bg-muted/30" : ""}`}
                  data-testid={`day-${format(day, "yyyy-MM-dd")}`}
                >
                  {/* Day Number */}
                  <div className={`text-sm font-medium mb-2 ${
                    isToday 
                      ? "bg-primary text-primary-foreground w-6 h-6 rounded-full flex items-center justify-center" 
                      : isCurrentMonth 
                        ? "text-foreground" 
                        : "text-muted-foreground"
                  }`}>
                    {format(day, "d")}
                  </div>

                  {/* Appointments */}
                  <div className="space-y-1">
                    {dayAppointments.slice(0, 3).map((appointment: any) => (
                      <div
                        key={appointment.id}
                        className="bg-chart-2/10 border border-chart-2 rounded px-2 py-1 text-xs"
                        data-testid={`month-appointment-${appointment.id}`}
                      >
                        <div className="font-medium text-chart-2 truncate">
                          {appointment.title}
                        </div>
                        <div className="text-muted-foreground">
                          {format(new Date(appointment.startsAt), "HH:mm")}
                        </div>
                      </div>
                    ))}
                    {dayAppointments.length > 3 && (
                      <div className="text-xs text-muted-foreground">
                        +{dayAppointments.length - 3} daha
                      </div>
                    )}
                  </div>
                </div>
              );
            })}
          </div>
        </Card>
      )}

      {/* Today's Appointments */}
      <Card className="p-6">
        <h4 className="font-semibold mb-4">Bug√ºn√ºn Randevularƒ±</h4>
        <div className="space-y-3">
          {appointments && appointments.length > 0 ? (
            appointments
              .filter((apt: any) => format(new Date(apt.startsAt), "yyyy-MM-dd") === format(new Date(), "yyyy-MM-dd"))
              .map((appointment: any) => (
                <div
                  key={appointment.id}
                  className="flex items-center justify-between p-3 bg-card rounded-lg border border-border"
                  data-testid={`today-appointment-${appointment.id}`}
                >
                  <div className="flex items-center gap-3">
                    <div className="w-3 h-3 bg-chart-2 rounded-full"></div>
                    <div>
                      <div className="font-medium">{appointment.title}</div>
                      <div className="text-sm text-muted-foreground">
                        {appointment.description || "A√ßƒ±klama yok"}
                      </div>
                    </div>
                  </div>
                  <div className="text-right">
                    <div className="text-sm font-medium">
                      {format(new Date(appointment.startsAt), "HH:mm")} - {format(new Date(appointment.endsAt), "HH:mm")}
                    </div>
                    <div className="text-xs text-muted-foreground">
                      {Math.round((new Date(appointment.endsAt).getTime() - new Date(appointment.startsAt).getTime()) / (1000 * 60))} dakika
                    </div>
                  </div>
                </div>
              ))
          ) : (
            <div className="text-center text-muted-foreground py-8">
              Bug√ºn i√ßin randevu yok
            </div>
          )}
        </div>
      </Card>
    </div>
  );
}

