  const [page] = useState(1);
  const [deleteClientId, setDeleteClientId] = useState<string | null>(null);

  const { data, isLoading } = useClients({ 
    search, 
    page, 
    pageSize: limit || 10 
  });

  const clients = data?.clients || [];
  const total = data?.total || 0;

  if (isLoading) {
    return <div>Y√ºkleniyor...</div>;
  }

  if (clients.length === 0 && !search) {
    return (
      <EmptyState
        title="Hen√ºz danƒ±≈üan yok"
        description="ƒ∞lk danƒ±≈üanƒ±nƒ±zƒ± ekleyerek ba≈ülayƒ±n"
      />
    );
  }

  return (
    <div className="space-y-4">
      {!limit && (
        <div className="flex items-center gap-4">
          <div className="flex-1">
            <div className="relative">
              <Search className="absolute left-3 top-1/2 transform -translate-y-1/2 w-4 h-4 text-muted-foreground" />
              <Input
                placeholder="Ad, email veya telefon ile ara..."
                value={search}
                onChange={(e) => setSearch(e.target.value)}
                className="pl-10"
                data-testid="input-client-search"
              />
            </div>
          </div>
          <Select>
            <SelectTrigger className="w-48" data-testid="select-status-filter">
              <SelectValue placeholder="T√ºm Durumlar" />
            </SelectTrigger>
            <SelectContent>
              <SelectItem value="all">T√ºm Durumlar</SelectItem>
              <SelectItem value="active">Aktif</SelectItem>
              <SelectItem value="inactive">Pasif</SelectItem>
              <SelectItem value="completed">Tamamlanmƒ±≈ü</SelectItem>
            </SelectContent>
          </Select>
          <Button variant="outline" size="icon">
            <Filter className="w-4 h-4" />
          </Button>
        </div>
      )}

      <div className="border rounded-md">
        <Table>
          <TableHeader>
            <TableRow>
              {!limit && (
                <TableHead className="w-12">
                  <Checkbox />
                </TableHead>
              )}
              <TableHead>Danƒ±≈üan</TableHead>
              <TableHead>ƒ∞leti≈üim</TableHead>
              <TableHead>Ya≈ü</TableHead>
              <TableHead>Aktif Plan</TableHead>
              <TableHead>Son Randevu</TableHead>
              <TableHead>Durum</TableHead>
              <TableHead>ƒ∞≈ülemler</TableHead>
            </TableRow>
          </TableHeader>
          <TableBody>
            {clients.map((client: any) => (
              <TableRow key={client.id} data-testid={`row-client-${client.id}`}>
                {!limit && (
                  <TableCell>
                    <Checkbox />
                  </TableCell>
                )}
                <TableCell>
                  <div className="flex items-center gap-3">
                    <div className="w-10 h-10 bg-primary/10 rounded-full flex items-center justify-center">
                      <span className="text-sm font-medium text-primary">
                        {client.firstName[0]}{client.lastName[0]}
                      </span>
                    </div>
                    <div>
                      <p className="font-medium" data-testid={`text-client-name-${client.id}`}>
                        {client.firstName} {client.lastName}
                      </p>
                      <p className="text-sm text-muted-foreground">
                        {client.goals?.join(", ") || "Hedef belirlenmemi≈ü"}
                      </p>
                    </div>
                  </div>
                </TableCell>
                <TableCell>
                  <div>
                    <p className="text-sm" data-testid={`text-client-email-${client.id}`}>
                      {client.email || "Email yok"}
                    </p>
                    <p className="text-sm text-muted-foreground" data-testid={`text-client-phone-${client.id}`}>
                      {client.phone || "Telefon yok"}
                    </p>
                  </div>
                </TableCell>
                <TableCell>
                  <span className="text-sm">
                    {client.birthDate ? calculateAge(client.birthDate) : "Bilinmiyor"}
                  </span>
                </TableCell>
                <TableCell>
                  <span className="text-sm text-muted-foreground">
                    Plan atanmamƒ±≈ü
                  </span>
                </TableCell>
                <TableCell>
                  <span className="text-sm text-muted-foreground">
                    Hen√ºz randevu yok
                  </span>
                </TableCell>
                <TableCell>
                  <Badge variant="outline" className="bg-muted text-muted-foreground">
                    Beklemede
                  </Badge>
                </TableCell>
                <TableCell>
                  <div className="flex items-center gap-2">
                    <Button 
                      variant="ghost" 
                      size="icon"
                      data-testid={`button-view-client-${client.id}`}
                    >
                      <Eye className="w-4 h-4" />
                    </Button>
                    <Button 
                      variant="ghost" 
                      size="icon"
                      data-testid={`button-edit-client-${client.id}`}
                    >
                      <Edit className="w-4 h-4" />
                    </Button>
                    <Button 
                      variant="ghost" 
                      size="icon"
                      onClick={() => setDeleteClientId(client.id)}
                      data-testid={`button-delete-client-${client.id}`}
                    >
                      <Trash2 className="w-4 h-4 text-destructive" />
                    </Button>
                  </div>
                </TableCell>
              </TableRow>
            ))}
          </TableBody>
        </Table>
      </div>

      {!limit && total > 10 && (
        <div className="flex items-center justify-between">
          <div className="text-sm text-muted-foreground">
            1-{Math.min(10, total)} / {total} danƒ±≈üan g√∂steriliyor
          </div>
          <div className="flex items-center gap-2">
            <Button variant="outline" size="sm" disabled>
              √ñnceki
            </Button>
            <Button variant="default" size="sm">1</Button>
            <Button variant="outline" size="sm">2</Button>
            <Button variant="outline" size="sm">3</Button>
            <Button variant="outline" size="sm">
              Sonraki
            </Button>
          </div>
        </div>
      )}

      <ConfirmDialog
        isOpen={!!deleteClientId}
        onClose={() => setDeleteClientId(null)}
        onConfirm={() => {
          // TODO: Implement delete
          setDeleteClientId(null);
        }}
        title="Danƒ±≈üanƒ± sil"
        description="Bu danƒ±≈üanƒ± silmek istediƒüinizden emin misiniz? Bu i≈ülem geri alƒ±namaz."
      />
    </div>
  );
}

